mapper.select:
SELECT o.id AS id, o.org_type_id AS orgTypeId, o.code_no AS codeNo, o.region_id AS regionId, r.name AS regionName, o.name AS name, o.short_name AS shortName, o.address AS address, o.en_name AS enName, o.en_short_name AS enShortName, o.en_address AS enAddress, o.web_url AS webUrl, o.contacts AS contacts, o.telephone AS telephone, o.fax AS fax, o.fast_code AS fastCode, o.display_order AS displayOrder, o.memo AS memo, o.status AS status, o.time_version AS timeVersion FROM ctr_orgs o LEFT JOIN ctr_regions r ON r.id = o.region_id WHERE o.org_type_id != 3 AND o.`status`=0 AND o.id NOT IN ( SELECT cor.child_id FROM ctr_org_relations AS cor WHERE cor.parent_id = #{queryDto.parentId} )
AND CONCAT_WS(o.code_no,o.name) LIKE CONCAT('%',#{queryDto.searchStr},'%')
AND r.id = #{queryDto.regionId}

SELECT o.id AS id, o.org_type_id AS orgTypeId, o.code_no AS codeNo, o.nacao_id AS nacaoId, o.region_id AS regionId, r.name AS regionName, o.name AS name, o.short_name AS shortName, o.address AS address, o.en_name AS enName, o.en_short_name AS enShortName, o.en_address AS enAddress, o.web_url AS webUrl, o.contacts AS contacts, o.telephone AS telephone, o.fax AS fax, o.fast_code AS fastCode, o.display_order AS displayOrder, o.memo AS memo, o.status AS status FROM ctr_orgs o LEFT JOIN ctr_regions r ON r.id = o.region_id WHERE 1=1
AND o.name = #{name}
AND o.org_type_id = #{orgType}
